package com.osama.mynotelanguages;import android.content.Context;import android.content.Intent;import android.graphics.ColorSpace;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import android.widget.Toast;import androidx.annotation.NonNull;import androidx.appcompat.app.AlertDialog;import androidx.recyclerview.widget.RecyclerView;import com.android.volley.AuthFailureError;import com.android.volley.Request;import com.android.volley.RequestQueue;import com.android.volley.Response;import com.android.volley.VolleyError;import com.android.volley.toolbox.StringRequest;import com.android.volley.toolbox.Volley;import java.util.ArrayList;import java.util.HashMap;import java.util.Map;import java.util.zip.Inflater;public class Customadapter extends RecyclerView.Adapter<Customadapter.MyViewHolder>   {    Context context;    ArrayList word , meaning , save;    ClickListener OnClickListener;    String type , email , token;    int position;    Customadapter(Context context , ArrayList word , ArrayList meaning ,String type, String token , String email ){        this.context = context;        this.word = word;        this.meaning = meaning;        this.type = type;        this.email = email;        this.token = token;    }    @NonNull    @Override    public MyViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        LayoutInflater inflater = LayoutInflater.from(context);        View view  = inflater.inflate(R.layout.words,parent,false);        return new  MyViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull MyViewHolder holder, int position) {        this.position = position;        holder.words.setText(String.valueOf(word.get(position)));        holder.meanings.setText(String.valueOf(meaning.get(position)));        holder.words.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                Intent intent  = new Intent(context,search.class);                intent.putExtra("word",String.valueOf(word.get(position)));                intent.putExtra("meaning" , String.valueOf(meaning.get(position)));                intent.putExtra("intent" , context.getApplicationContext().toString());                context.startActivity(intent);            }        });        holder.view.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                add_data_base(String.valueOf(meaning.get(position)) , String.valueOf(word.get(position)) ,type , token , email  );            }        });    }    @Override    public int getItemCount() {        return word.size();    }    public interface ClickListener{        void OnClick(View view , int position);    }    public class MyViewHolder extends RecyclerView.ViewHolder {        TextView words , meanings;        ImageView view;        public MyViewHolder(@NonNull View itemView) {            super(itemView);            words = itemView.findViewById(R.id.words);            meanings = itemView.findViewById(R.id.meanings);            view = itemView.findViewById(R.id.save_);        }    }    public void add_data_base(String string1 , String string2 , String type , String token , String email){        StringRequest request = new StringRequest(Request.Method.POST, "https://osamaelkassaby.com/apps/note/add/", new Response.Listener<String>() {            @Override            public void onResponse(String response) {android.app.AlertDialog.Builder builder = new android.app.AlertDialog.Builder(context);                builder.setIcon(R.drawable.sucsess)                        .setView(R.layout.sucsess);                android.app.AlertDialog alertDialog = builder.create();                alertDialog.show();            }        }, new Response.ErrorListener() {            @Override            public void onErrorResponse(VolleyError error) {                AlertDialog.Builder builder = new AlertDialog.Builder(context);                builder.setTitle("Error")                        .setIcon(R.drawable.ic_baseline_error_24)                        .setMessage("Error In connection");                AlertDialog dialog = builder.create();                dialog.show();            }        }){            @Override            protected Map<String, String> getParams() throws AuthFailureError {                Map<String, String> data = new HashMap<>();                data.put("type", type);                data.put("token", token);                data.put("email", email);                data.put("meaning",string1);                data.put("word",string2 );                return data;            }        };        RequestQueue requestQueue = Volley.newRequestQueue(context);        requestQueue.add(request);    }}