package com.osama.mynotelanguages;import android.app.Activity;import android.app.AlertDialog;import android.view.LayoutInflater;public class LodingDailog {    Activity activity;    AlertDialog alertDialog;    LodingDailog(Activity MYactivity){        activity =  MYactivity;        }        void  StartLodingAlertDialog(){        AlertDialog.Builder builder =  new AlertDialog.Builder(activity);            LayoutInflater inflater = activity.getLayoutInflater();            builder.setView(inflater.inflate(R.layout.coustom_dailog , null));            builder.setCancelable(true);            alertDialog = builder.create();            alertDialog.show();        }        void dismiss(){        alertDialog.dismiss();        }    }