package com.osama.mynotelanguages;import androidx.appcompat.app.AlertDialog;import androidx.appcompat.app.AppCompatActivity;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import android.database.Cursor;import android.os.Bundle;import android.widget.TextView;import java.util.ArrayList;public class show_backup extends AppCompatActivity {    RecyclerView recyclerView;    Customadapter customAdapter;    ArrayList<String> word , meaning;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_show_backup);        word = new ArrayList<>();        meaning = new ArrayList<>();        TextView textView = findViewById(R.id.type_lang);        if (!getIntent().hasExtra("intent")) {            AlertDialog.Builder builder = new AlertDialog.Builder(show_backup.this);            builder.setTitle("Error")                    .setIcon(R.drawable.ic_baseline_error_24)                    .setMessage("NO DATA");            AlertDialog dialog = builder.create();            dialog.show();        }else{            String intent = getIntent().getStringExtra("intent");            if (intent.equals("en")){                textView.setText("Backup " + "english");                BackUp_en backUp_en = new BackUp_en(show_backup.this);                Cursor cursor = backUp_en.redData();                if (cursor.getCount() == 0) {                    AlertDialog.Builder builder = new AlertDialog.Builder(show_backup.this);                    builder.setIcon(R.drawable.ic_baseline_error_24)                            .setMessage("No Date");                    AlertDialog dialog = builder.create();                    dialog.show();                } else {                    while (cursor.moveToNext()) {                        word.add(cursor.getString(0));                        meaning.add(cursor.getString(1));                        recyclerView = findViewById(R.id.re);                        customAdapter = new Customadapter(show_backup.this, word, meaning ,"en" , "" ,"");                        recyclerView.setAdapter(customAdapter);                        recyclerView.setLayoutManager(new LinearLayoutManager(show_backup.this));                    }                }            }else if(intent.equals("fr")){                textView.setText("Backup " + "Franch");                BackUp_fr backUp_en = new BackUp_fr(show_backup.this);                Cursor cursor = backUp_en.redData();                if (cursor.getCount() == 0) {                    AlertDialog.Builder builder = new AlertDialog.Builder(show_backup.this);                    builder.setIcon(R.drawable.ic_baseline_error_24)                            .setMessage("No Date");                    AlertDialog dialog = builder.create();                    dialog.show();                } else {                    while (cursor.moveToNext()) {                        word.add(cursor.getString(0));                        meaning.add(cursor.getString(1));                        recyclerView = findViewById(R.id.re);                        customAdapter = new Customadapter(show_backup.this, word, meaning , "" , "" , "");                        recyclerView.setAdapter(customAdapter);                        recyclerView.setLayoutManager(new LinearLayoutManager(show_backup.this));                    }            }        }    }}}